		System.out.println(" **   ** " + " " + " **   ** " + " " + "  **     **" + " " + "** ");
		System.out.println(" **   ** " + " " + " **   ** " + " " + "  **    **" + " " + "  ** ");
		System.out.println(" **   ** " + " " + " **   ** " + " " + "  ******   " + " " + "  *** ");
		System.out.println(" **   ** " + " " + " **   ** " + " " + "  **    " + " " + "        ** ");
		System.out.println(" **   ** " + " " + " **   ** " + " " + "  **    " + " " + "         ** ");
		System.out.println("  ** ** " + " " + "   ** ** " + " " + "   **    " + " " + "        ** ");
		System.out.println("   *** " + " " + "     *** " + " " + "    **    " + " " + "   *****");
